<script>import MarkdownTokens from "./MarkdownTokens.svelte";
import { defaultOptions, defaultRenderers, parse } from "./markedConfiguration";
import { suppressWarnings } from "./suppressWarnings";
suppressWarnings();
export let source;
export let options = {};
export let renderers = {};
let tokens;
let actualRenderers;
let actualOptions;
$: {
  tokens = parse(source);
  actualRenderers = { ...defaultRenderers(), ...renderers };
  actualOptions = { ...defaultOptions(), ...options };
}
</script>

<MarkdownTokens {tokens} renderers={actualRenderers} options={actualOptions} />
